.fbconsultLoadingIndicator{
  margin: 0px auto 0;
  margin-top: -60px;
  width: 180px;
  text-align: center;
}

.fbconsultLoadingIndicator > div{
  width: 25px;
  height: 25px;
    margin: 0 4px;
  background-color: var(--link-color);

  border-radius: 100%;
  display: inline-block;
  -webkit-animation: sk-bouncedelay 1.4s infinite ease-in-out both;
  animation: sk-bouncedelay 1.4s infinite ease-in-out both;
}

.fbconsultLoadingIndicator .bar5 {
  -webkit-animation-delay: -0.16s;
  animation-delay: -0.16s;
}

.fbconsultLoadingIndicator .bar4 {
  -webkit-animation-delay: -0.32s;
  animation-delay: -0.32s;
}

.fbconsultLoadingIndicator .bar3 {
  -webkit-animation-delay: -0.48s;
  animation-delay: -0.48s;
}

.fbconsultLoadingIndicator .bar2 {
  -webkit-animation-delay: -0.64s;
  animation-delay: -0.64s;
}

.fbconsultLoadingIndicator .bar1 {
  -webkit-animation-delay: -0.80s;
  animation-delay: -0.80s;
}

@-webkit-keyframes sk-bouncedelay {
  0%, 80%, 100% { -webkit-transform: scale(0) }
  40% { -webkit-transform: scale(1.0) }
}

@keyframes sk-bouncedelay {
  0%, 80%, 100% { 
    -webkit-transform: scale(0);
    transform: scale(0);
  } 40% { 
    -webkit-transform: scale(1.0);
    transform: scale(1.0);
  }
}
